import './productcard.scss'
import { useHistory } from "react-router-dom";

const ProductCard = ({name, image, productId}) => {
    let history = useHistory();
    return(
        <div className='product-card' onClick={()=>history.push(`/products/${productId}`)} style={{
            background: `url(${image}) center center no-repeat`,
            backgroundSize: 'cover'
        }}>
            <div className='hover-mask' />
            <div className='card-hidden-footer'><div>CLICK AND CHECK</div></div>
            <div className='card-footer'>{name}</div>
        </div>
    )
}

export default ProductCard
