import { useState, useEffect } from 'react';
import './navigation.scss'
import {Link} from 'react-router-dom'
import UrbanBinLogo from '../../assets/urbanbin-logo.svg'

const Navigation = ({ navType }) => {

    const routesArray = [
        {
            path: '/',
            route: 'HOME'
        },
        {
            path: '/about',
            route: 'COMPANY'
        },
        {
            path: '/products',
            route: 'PRODUCTS'
        },
        {
            path: '/contact',
            route: 'CONTACT US'
        }
    ]
    
    const handleScrollChange = () => {
        let position = document.querySelector('body').getBoundingClientRect();
        const navbar = document.querySelector('.navigation')
        position.y < -1 ?
            navbar.style.height='105px'
        :
            navbar.style.height='130px'
    }

    const [isMounted, changeMountedState] = useState(false, () => {
        if(isMounted) {
            console.log('mounted')
            window.addEventListener('scroll', handleScrollChange);
        } else {
            window.removeEventListener('scroll', handleScrollChange)
        }
    })

    useEffect(()=> {
        changeMountedState(true)
        // if(isMounted){ // dodane z powodu tego, że komponent nie dopuszczał do wyrenderowania małego paska nawigacyjnego bo próbował odpalić funkcję handleScrollChange. NavType to argument przekazany z App który zawiera 'large' lub 'small' - w zależności jaki pasek powinien zostać wyświetlony
        //    window.addEventListener('scroll', handleScrollChange) 
        // }
        // return () => {
        //     console.log('event-listener clearing')
        //     window.removeEventListener('scroll', handleScrollChange)
        // }
    }, [navType])

    return (
        <nav className='navigation'>
            <div className='urbanbin-logo'>
                <Link to='/'><img id='logo' alt='company-logo' src={UrbanBinLogo} /></Link>
            </div>
            <div className='navigation-routes'>
                {
                    routesArray.map(({path, route, routeFirst}, index) => {
                        return(
                            <div key={index} className='nav-route'>
                                <Link to={path}>
                                    <div className='route-content'>
                                       {route}
                                    </div>
                                    <div className='underline'/>
                                </Link>
                            </div>
                        )
                    })
                }
            </div>
        </nav>
    )
}

export default Navigation;
