import { useState } from 'react'
import { fallDown as Menu } from 'react-burger-menu'
import { Link } from 'react-router-dom'
import './burgerLibStyles.scss'
import './mobilenavigation.scss'
import UrbanBinLogo from '../../assets/urbanbin-logo.svg'

const MobileNavigation = () => {

    const [menuStatus, setMenuStatus] = useState(true)

    const handleMenuOpen = () => {
        setMenuStatus(true)
    }

    const handleMenuClose = () => {
        if(menuStatus){
            setMenuStatus(false)
        } else if(menuStatus === false) {
            setMenuStatus(true)
        }
            
    }

    return (
        <Menu onOpen={handleMenuOpen} isOpen={menuStatus} pageWrapId={ "section" } outerContainerId={ "app" } itemListClassName={ "item-list" } right width={'280px'}>
        <nav id='nav-wrapper'>
            <div className='small-logo'>
                <img src={UrbanBinLogo} alt='inner-menu-company-logo'/>
            </div>
            <div className='items-wrapper'>
                <div className="menu-item"><Link className='link' onClick={handleMenuClose} to='/'>HOME</Link></div>
                <div className="menu-item"><Link className='link' to='/about'>ABOUT US</Link></div>
                <div className="menu-item"><Link className='link' to='/projects'>PRODUCTS</Link></div>
                <div className="menu-item"><Link className='link' to='/services'>CONTACT</Link></div>
                <div className="menu-item"><i className="fab fa-facebook fa-lg"/></div>
            </div>
        </nav>
  </Menu>
    )
}

export default MobileNavigation
