import { useState, useEffect } from 'react'
import './App.scss';
import {Switch, Route} from 'react-router-dom';
import Navigation from '../components/Navigation/Navigation.component'
import About from '../pages/About/About.component'
import Home from '../pages/Home/Home.component'
import Contact from '../pages/Contact/Contact.component'
import Products from '../pages/Products/Products.component'

const App = () => {

  const [navType, setNav] = useState(true); // true is big nav and false small

  const windowW= window.innerWidth;

  const changeNavBar = () => { // change nav-bar when resizing window
    const navBarRender = window.matchMedia("(max-width: 768px)")
      navBarRender.matches ? setNav('small') : setNav('large');
  }

  useEffect(() => { // check if window width is less or equal 621px and do action depending on this data
    windowW <= 768 ?
      setNav('small')
      :
      setNav('large')
  }, [windowW])

  window.addEventListener('resize', changeNavBar);


  return (
    <div className='App'>
    {
      navType ? 

      <Navigation />
      :
      null
    }
      <Switch>
        <Route exact path='/' component={Home} />
        <Route exact path='/about' component={About} />
        <Route exact path='/contact' component={Contact} />
        <Route path='/products' component={Products} />
      </Switch>
    </div>
  );
}

export default App;
