import './contactusform.scss'
import {useState} from 'react'

const ContactUsForm = ({label, type}) => {
    
    const [mailName, setMailName] = useState('')
    const [guestMail, setGuestMail] = useState('')
    const [mailText, setMailText] = useState('')

    return (
        <div className='mail-form'>
            {
                type === 'textarea' ? 
                <div className='mail-form-textarea'>
                    <label className='mail-input-textarea-label'>{label}</label>
                    <input className='mail-input-textarea' type={type} />
                </div>
                :            
                <div className='mail-form-group'>
                    <input className='mail-input'/>
                    <label className={`${mailName.length ? 'shrink' : ''} mail-input-label`}>{label}</label>
                </div>
            }

            {/* <div className='mail-form-group'>
                <input className='mail-input' type='email' value={guestMail}/>
                <label className='mail-input-label'>Your e-mail</label>
            </div> */}
        </div>
    )
}

export default ContactUsForm
